import React from 'react'
import axios from 'axios';

class User extends React.Component {
    constructor() {
        super()
        this.state = {
            mfa: true,
            email: ''
        }
    }

    handleChange = (e) => {
        const isChecked = e.target.checked
        axios.put('/users/mfa', { mfa: isChecked }, { headers: { 'x-auth': localStorage.getItem('x-auth') } })
            .then((response) => {
                this.setState(() => ({
                    mfa: response.data.mfa
                }))
            })
    }

    render() {
        return (
            <form>
                <h2>User info</h2>
                <label>Mfa  <input type="checkbox" checked={this.state.mfa} onChange={this.handleChange} /> </label>
                <label>{this.state.email}</label>
            </form>
        )
    }

    componentDidMount() {
        axios.get('/users', { headers: { 'x-auth': localStorage.getItem('x-auth') } })
            .then((response) => {
                this.setState(() => ({ mfa: response.data.mfa, email: response.data.email }))
            })
    }
}

export default User
