import React from 'react'
import axios from 'axios';
var QRCode = require('qrcode.react');

class User extends React.Component {
    constructor() {
        super()
        this.state = {
            mfa: true,
            email: ''
        }
    }

    handleChange = (e) => {
        const isChecked = e.target.checked
        axios.put('/users/mfa', { mfa: isChecked }, { headers: { 'x-auth': localStorage.getItem('x-auth') } })
            .then((response) => {
                this.setState(() => ({
                    mfa: response.data.mfa.on
                }))
            })
    }

    render() {
        return (
            <form>
                <div className="border p-5 m-auto rounded shadow text-center" style={{ 'max-width': '380px' }}>
                    <h2 className="mb-2">Settings</h2>
                    <QRCode className="mt-5 mb-5" value={localStorage.getItem('qr')} /> <br />
                        <h5 className="">{this.state.email}</h5>
                        <label><input type="checkbox" checked={this.state.mfa} onChange={this.handleChange} />  Multi-Factor Authentication </label>
                </div>
            </form>
                )
            }

    componentDidMount() {
                    axios.get('/users', { headers: { 'x-auth': localStorage.getItem('x-auth') } })
                        .then((response) => {
                            this.setState(() => ({ mfa: response.data.mfa, email: response.data.email }))
                        })
                }
                }

export default User
