import React from 'react'
import axios from 'axios'

class App extends React.Component{

  constructor(){
    super()
    this.state = {
      user:'',
      password: ''
    }
  }

  handleChange = (e)=>{
      e.persist()
      this.setState(()=>({[e.target.name]: e.target.value}))
  }

  handleSubmit = (e)=>{
    e.preventDefault()
    axios.post('/users/login', {user:this.state.user, password: this.state.password})
    .then((response)=>{
      console.log(response.data)
    })
  }

  render(){
    return(
      <form onSubmit={this.handleSubmit}>
        <input value={this.state.user} name="user" onChange={this.handleChange} type='text' placeholder='enter email/mobile'/>
        <input value={this.state.password} name="password" onChange={this.handleChange} type='password' />
        <button>Submit</button>
      </form>
    )
  }
}

export default App
