import React from 'react'
import axios from 'axios'

class UserRegister extends React.Component {

    constructor() {
        super()
        this.state = {
            email: '',
            mobile: '',
            password: ''
        }
    }

    handleChange = (e) => {
        e.persist()
        this.setState(() => ({ [e.target.name]: e.target.value }))
    }

    handleSubmit = (e) => {
        e.preventDefault()
        axios.post('/users', { email: this.state.email,mobile:this.state.mobile, password: this.state.password })
            .then((response) => {
                if(response.data.token){
                    this.props.history.push('/users')
                    localStorage.setItem('x-auth', response.data.token)
                }
            })
            .catch((err) => {
                console.log(err.response.data.error)
            })
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <input value={this.state.user} name="email" onChange={this.handleChange} type='text' placeholder='enter email' />
                <input value={this.state.mobile} name="mobile" onChange={this.handleChange} type='text' placeholder='enter mobile' />
                <input value={this.state.password} name="password" onChange={this.handleChange} type='password' placeholder='enter password'/>
                <button>Submit</button>
            </form>
        )
    }
}

export default UserRegister
