import React from 'react'
import axios from 'axios';
import User from './User';

class UserLoginMultiAuth extends React.Component{

    constructor(){
        super()
        this.state = {
            mfa:''
        }
    }

    handleSubmit = (e) => {
        e.preventDefault()
        console.log(localStorage.getItem('otp-auth'))
        axios.post('http://localhost:3005/users/login/multi-auth',{otp:this.state.mfa}, {headers:{'otp-auth':localStorage.getItem('otp-auth')}})
        .then((response)=>{
            localStorage.setItem('x-auth', response.data)
            this.props.history.push('/users')
        })
    }

    handleChange = (e) => {
        e.persist()
        this.setState(() => ({ mfa: e.target.value }))
    }

    render(){
        return(
            <form onSubmit={this.handleSubmit}>
            <input type="text" placeholder="enter mfa otp" onChange={this.handleChange} value={this.state.mfa} />
            <button>Submit</button>
            </form>
        )
    }
}

export default UserLoginMultiAuth
