import React from 'react'
import axios from 'axios';

class UserLoginMultiAuth extends React.Component{

    constructor(){
        super()
        this.state = {
            mfa:''
        }
    }

    handleChange = (e) => {
        axios.post('/users/login/multi-auth',{otp:this.state.mfa}, {headers:{'otp-auth':localStorage.getItem('otp-auth')}})
        .then((token)=>{
            console.log(token)
        })
    }

    handleChange = (e) => {
        e.persist()
        this.setState(() => ({ [e.target.name]: e.target.value }))
    }

    render(){
        return(
            <form onSubmit={this.handleSubmit}>
            <input type="text" placeholder="enter mfa otp" onChange={this.handleChange} value={this.state.mfa} />
            </form>
        )
    }
}
